import React from "react";

import {
  GroupContainer,
  FormInputContainer,
  FormInputLabel,
} from "./from-input.styles";

const FormInput = ({ handleChange, label, ...otherProps }) => (
  <GroupContainer>
    <FormInputContainer onChange={handleChange} {...otherProps} />
    {label && (
      <label
        className={`${
          otherProps.value.length ? "shrink" : ""
        } form-input-label`}
      >
        {label}
      </label>
    )}
  </GroupContainer>
);

export default FormInput;
